    private void btncalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btncalcularActionPerformed
        // TODO add your handling code here:
        long valor=Integer.parseInt(txtvalor.getText());
        long valor2=valor;
        valor=ClasseFatorial.setValor(valor);
        String resultado=String.valueOf(valor);
        if(valor!=0) {
            if(valor2<=20) {
                lblresultado2.setText(resultado);
            } else {
                lblresultado2.setText("Resultado indisponivel, Apenas formula");
            }
            resultado=ClasseFatorial.getFormula(valor2);
            lblformula2.setText(resultado);
        } else {
            lblresultado2.setText("Invalido!");
            lblformula2.setText("Invalido!");
        }
    }//GEN-LAST:event_btncalcularActionPerformed

